    private void spinStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_spinStateChanged
        
        int valor_atual = Integer.parseInt(String.valueOf(spin.getValue()));
        int fat = 1;
        if (valor_atual==0){
            lbl_fat.setText("0");
        } else{
            for(int c=valor_atual; c>=1;c--){
            fat *=c;
            }
            lbl_fat.setText(Integer.toString(fat));
        }
        
    }//GEN-LAST:event_spinStateChanged

